import React from "react";
import axios from "axios";


export default class Return extends React.Component{
    constructor(props){
        super(props);
        this.state={
            originCity:"",
            destinationCity:"",
            departureDate:"",
            order:0

        }
    }
    Change=(e)=>{
        this.setState({
          [e.target.name]:e.target.value
        })
      }
      componentDidMount(){
		axios({
			method: 'get',
			url: `http://localhost:5000/flightdetails?price=price&order=${this.state.order}&originCity=${this.state.originCity}&destinationCity=${this.state.destinationCity}`    		
        })

		.then((response) =>{
			console.log(response)
           this.setState({
				result:response.data

            });	
            console.log(response);

        })

		.catch((err) => alert(err))
	}


    onSubmit(e) {
      e.preventDefault();
     // console.log(this.state.code , this.state.name ,this.state.email)
     // console.log(this.state.arr);

      //const data={name:this.state.name,order:this.state.order}
      //console.log(data);
      //const arr=[...this.state.arr, data];
      //console.log(arr);
      //const json=JSON.stringify(arr);
      //console.log(json);
      //localStorage.setItem("xyz",json);
    //   axios.post(``,data)
    //   .then(res => {
    //     console.log(res);
    //     //console.log(res);
    //   })


    }
    render(){
        console.log(this.state.originCity)
        console.log(this.state.destinationCity)
        console.log(this.state.departureDate)
        console.log(this.state.order)
        return(
            <React.Fragment>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                    <div class="row">
    <div className="col">
    <label>Current Place</label>
      <input type="text" class="form-control" placeholder="From" name="originCity" value={this.state.originCity} onChange={this.Change}/>
    </div>
    <div className="col">
        <label>Destination </label>

      <input type="text" class="form-control" placeholder="To" name="destinationCity" value={this.state.destinationCity} onChange={this.Change}/>
    </div>
    <div className="col">
        <label>DepartureDate</label>
      <input type="date" class="form-control" placeholder="departureDate" name="departureDate" value={this.state.departureDate} onChange={this.Change}/>
    </div>
    <div className="col">
        <label>Price</label>
        <select name="order" value={this.state.order} onChange={this.Change}>
            <option>Select Price</option>
            <option value="1">Price Low to high</option>
            <option value="-1">Price high to low</option>
        </select>
    </div>
    <div className="col">
    <input type="submit"  className="btn btn-primary mt-5"/>
    </div>
  </div>
                    </div>
                    {/* <div className="form-group">
                        <label>Enter filter consition (if you want):  </label>
                        <input type="text" className="form-control"  value={this.state.filter} onChange={this.onChangeFilter} />
                    </div> */}


                </form>
            </React.Fragment>
        )
    }
}
